INSERT INTO `weapon_quality` (weapon_quality_id, quality_name, active, ranked, effect, book_index) VALUES
  (1, 'Accurate', 'Passive', 1,
   'For each rank of this trait, the attacker adds [BOOST] to his attack dice pools while using this weapon.',
   'A-CRB:168, E-CRB:154, F-CRB:161'),
  (2, 'Auto-Fire', 'Active', 0, 'Increase the difficulty of the attack by [DIFFICULTY]. If the attack hits the attacker can trigger Auto-fire. Auto-fire can be triggered multiple times.Each time the attacker triggers Auto-fire it deals an additional hit to the target.', 'A-BGR:32, A-CRB:168, E-BGR:32, E-CRB:155, F-BGR:32, F-CRB:161'),
  (3, 'Blast', 'Active', 1, 'If the attack is successful and Blast activates, each character (friend or foe) Engaged with the original target sufferes wounds equal to the weapon\'s Blast rating.', 'A-BGR:32, A-CRB:168, E-BGR:32, E-CRB:155, F-BGR:32, F-CRB:162'),
  (4, 'Breach', 'Passive', 1, 'Ignore one point of Armor for every rank of Breach', 'A-BGR:32, A-CRB:168, E-BGR:32, E-CRB:155, F-BGR:32, F-CRB:162'),
  (5, 'Burn', 'Active', 1, 'If the attack is successful, the target continues to take the weapon\'s base damage for a number of rounds equal to the weapon\'s Burn rating.', 'A-CRB:169, E-CRB:155, F-CRB:162'),
  (6, 'Concussive', 'Active', 1, 'The target is staggered for a number of rounds equal to the weapon\'s Concussive rating.', 'A-CRB:169, E-CRB:155, F-CRB:162'),
  (7, 'Cortosis', 'Passive', 0, 'Weapons with the Cortosis quality are immune to the Sunder quality.', 'A-CRB:169, E-CRB:155, F-CRB:162'),
  (8, 'Cumbersome', 'Passive', 1, 'The character needs a Brawn characteristic equal to or greater than the weapon\'s Cumbersome rating.', 'A-BGR:32, A-CRB:169, E-BGR:32, E-CRB:155, F-BGR:32, F-CRB:163'),
  (9, 'Defensive', 'Passive', 1, 'A character wielding a weapon with the Defensive quality increases his melee defense by the weapons\'s Defensive rating.', 'A-CRB:169, E-CRB:156, F-CRB:163'),
  (10, 'Deflection', 'Passive', 1, 'An item with the Deflection quality increases the wearer\'s ranged defense equal to its Deflection rating.', 'A-CRB:169, E-CRB:156, F-CRB:163'),
  (11, 'Disorient', 'Active', 1, 'The target is disoriented for a number of rounds equal to the weapon\'s Disorient rating.', 'A-BGR:32, A-CRB:169, E-BGR:32, E-CRB:156, F-BGR:32, F-CRB:163'),
  (12, 'Ensnare', 'Active', 1, 'The target is immobilized for a number of rounds equal to the weapon\'s Ensnare rating.', 'A-CRB:169, E-CRB:156, F-CRB:163'),
  (13, 'Guided', 'Active', 1, 'Weapon can make an attack check at the end of the round, the check\'s Ability dice are equal to the weapon\'s Guided rating.', 'A-CRB:169, E-CRB:156, F-CRB:163'),
  (14, 'Inaccurate', 'Passive', 1, 'Add [SETBACK] to the attacker\'s dice pool equal to their Inaccurate rating.', 'A-CRB:169, E-CRB:156, F-CRB:163'),
  (15, 'Inferior', 'Passive', 0, 'Generates automatic [THREAT] on all checks.', 'A-CRB:170, E-CRB:156, F-CRB:163'),
  (16, 'Ion', 'Passive', 0, 'Damage is dealt as System Strain (Vehicles) or Strain Threshold (Droids)', 'A-BGR:32, A-CRB:170, E-BGR:32, E-CRB:156, F-CRB:163'),
  (17, 'Knockdown', 'Active', 0, 'The target is kFALSEcked prone.', 'A-BGR:32, A-CRB:169, E-BGR:32, E-CRB:156, F-BGR:32, F-CRB:163'),
  (18, 'Limited Ammo', 'Passive', 1, 'May be used to make a number of attacks equal to it\'s Limited Ammo rating before it must be reloaded.', 'A-BGR:32, A-CRB:170, E-BGR:32, E-CRB:156, F-BGR:32, F-CRB:163'),
  (19, 'Linked', 'Active', 1, 'On a successful attack the wielder may activate to gain an additional hit and may do so a number of times equal to the weapon\'s Linked rating.', 'A-BGR:32, A-CRB:170, E-BGR:32, E-CRB:156, F-CRB:164'),
  (20, 'Pierce', 'Passive', 1, 'Ignores one point of Soak for each rank of Pierce', 'A-BGR:32, A-CRB:170, E-BGR:32, E-CRB:157, F-BGR:32, F-CRB:164'),
  (21, 'Prepare', 'Passive', 1, 'The user must perform a number of Prepare maneuvers equal to the weapon\'s Prepare rating before making attacks.', 'A-CRB:170, E-CRB:157, F-CRB:164'),
  (22, 'Slow-Firing', 'Passive', 1,
   'Weapon must waith a number of rounds equal to its Slow-Firing rating before firing again.',
   'A-CRB:170, E-CRB:157, F-CRB:164'),
  (23, 'Stun', 'Active', 1, 'Weapon causes Strain to the target', 'A-CRB:170, E-CRB:157, F-CRB:164'),
  (24, 'Stun Damage', 'Passive', 0, 'Weapon deals damage as strain instead of wounds.',
   'A-BGR:32, A-CRB:170, E-BGR:32, E-CRB:157, F-BGR:32, F-CRB:164'),
  (25, 'Stun Setting', 'Passive', 0, 'Can switch weapon to Stun Damage', 'E-CRB:144'),
  (26, 'Sunder', 'Active', 0, 'Damages opposing weapon one step.', 'A-CRB:170, E-CRB:157, F-CRB:164'),
  (27, 'Superior', 'Passive', 0, 'Generates automatic [ADVANTAGE] on all checks', 'A-CRB:170, E-CRB:157, F-CRB:164'),
  (28, 'Tractor', 'Passive', 0,
   'Target may not move unless it makes a successful Piloting check with a difficulty based on the tractor beam\'s rating.',
   'A-CRB:171, E-CRB:157, F-CRB:164'), (29, 'Unwieldy', 'Passive', 1,
                                        'To wield correctly, character needs Agility characteristic equal or greater than rating.',
                                        'F-CRB:164'),
  (30, 'Vicious', 'Passive', 1, 'Add 10 times the Vicious rating to the critical roll.',
   'A-CRB:171, E-CRB:157, F-CRB:164');


INSERT INTO `npc_abilities` (npc_abilities_id, ability_name, description, book_index) VALUES (1, 'Acidic drool',
                                                                                              'As an incedental, spend a dark side Destiny Point to give the toothy bite weapon the Burn 2 quality until the end of the [SPECIES]\'s turn; this is a chemical burn.',
                                                                                              'LoNH:45'),
  (2, 'Additional Limbs', 'Xexto have six limbs, two legs and four armsl as a result, gain an additional free maneuver per turn, though still may not perform more than two maneuvers per turn.', 'JoY:46'),
  (3, 'Advanced Language Module', 'When encountering a new speries or language not in its databases, a TC-series protocol droid may make an Easy Knowledge [Education] check to learn enough of the new dilect to communicate.', 'DA:56'),
  (4, 'Airborne', '[SPECIES] can exist in an atmosphere without ever needing to land on solid ground.', 'JoY:51'),
  (5, 'All the Luck in the Galaxy', 'When making a check to gamble, a Deception check, or a Negotiation check, add [FORCE] to the check. Each [LIGHT] result automatically adds [SUCCESS] to the check. [DARK][DARK] results automatically add [DISPAIR] to the check', 'F-CRB:412'),
  (6, 'Alliance Leader', 'Alliance operatives add [BOOST] to all Vigilance and PErception checks when in the NPC\'s presence', 'A-CRB:414'),
  (7, 'Ammonia Breather', 'Breathes ammonia; must use a respirator, and if exposed to oxygen, treats it as a dangerous atsmophere with a rating 8', 'F-CRB:403'),
  (8, 'Amphibious', '[SPECIES] may breathe underwater without penalty and never suffer movement penalties for traveling through water.', 'A-BGR:47'),
  (9, 'Anatomical Knowledge', 'Anx have an innate grasp of how bodies are put togther and are able to intuit the anatomical vulnerabilities of almost any species. Anx gain one rank of the Lethal Blows talent.', 'SoF:19'),
  (10, 'Animal Combatant', 'Upgrade Ability dice when fighting wild creatures once', 'BtR:62'),
  (11, 'Aquatic Creature', '[SPECIES] suffer no movement penalties in aquatic environments.', 'SoF:43'),
  (12, 'Aquatic only Creature', '[SPECIES] suffer no penealties for moving through water and can breate underwater, but cannot move or survive on land.', 'SoF:35'),
  (13, 'Arboreal Creature', 'Tarvixes treat trees and branches as normal terrain and do not make checks or spend extra maneuvers in order to move along them vertically or horizontally.', 'CotG:24'),
  (14, 'Arboreal Predator', 'Bearsloths treat trees and branches as normal terrain and do not make checks or spend extra maneuvers in order to move along them vertically or horizontally.', 'CotG:22'),
  (15, 'Asharl Pelt', 'Remove [SETBACK] on Survival checks due to cold environments.', 'NoP:14'),
  (16, 'Awe-inspiring', 'Noira is such an aws-inspiring figure that anyone attempting to attack her suffers [SETBACK] to their check unless they spend a Destiny Point; use of the Destiny Point for this purpose makes the character in question immune from this ability', 'MPQ:91'),
  (17, 'Awkward', '[SPECIES] have great physical strength but their bulk imposes severe limitations in flexibility and agility. They add [SETBACK][SETBACK][SETBACK] to all Brawl, Melee, and Coordination checks they\'re required to make.', 'E-CRB:407'),
  (18, 'Backup Entertainer', 'When a SDMN Session Droid performs the Assist maneuver to help a character make a check as part of a performance, that character adds automatic [ADVANTAGE][ADVANTAGE] to the results instead of the usual benifits.', 'FH:50'),
  (19, 'Bad Knee', 'If TH-313 generates 3[THREAT] on [DISPAIR] on an Athletics, Brawl, or Melee check, he is immobilized for one round.', 'CotG:65'),
  (20, 'Balrekk\'s Butchery', 'As an incidental, the GM may spend a dark side Destiny point for Balrekk to gain Vicious +2 on all his weapons until the end of his turn.', 'OaA:38'),
  (21, 'Barbed Hide', 'When a rass is hit by a melee attack, the GM can spend [THREAT][THREAT] to inflict 1 wound [ignoring soak] on the attacker.', 'LoNH:73'),
  (22, 'Beast of Burden 10', '[SPECIES] add 10 to their encumbrance threshold', 'SoT:83'),
  (23, 'Beast of Burden 20', '[SPECIES] add 20 to their encumbrance threshold.', 'SoT:86'),
  (24, 'Beast of Burden 4', '[SPECIES] add 4 to their encumbrance threshold', 'NoP:51'),
  (25, 'Beast of Burden 5', '[SPECIES] add 5 to their encumbrance threshold.', 'SoT:85'),
  (26, 'Beguiling Pheromones', 'By emitting pheromones and altering their skin color, [SPECIES] can affect emotional states of other sentients. Once per check as an incidental, a [SPECIES] may suffer 2 strain to upgrade the ability of a Charm, Deception, or Negotiation check against a living sentient being within short range once. This ability has no effect on targets wearing breath masks or without respiratory systems.', 'FC:20'),
  (27, 'Berserk Rage', 'When the Savrip has sffered any wounds, it deatls +1 damage to Brawl and Melee attacks; when the Savrip is Critically Injured, it instead deals +2 damage to Brawl and Melee attacks', 'MPQ:70'),
  (28, 'Binary Processing', 'Cereans treat all Knowledge skills as carrer skills', 'F-CRB:55'),
  (29, 'Bite at Their Heels', 'When engaged with a target, allies recieve [BOOST] to combat checks made against the engaged target.', 'LoNH:77'),
  (30, 'Blessing of the Acients', 'Once per game session as an incidental, the elder may have a character within short range heal 4 wounds.', 'LoNH:69'),
  (31, 'Blind', 'This creature relies on smell and vibration to find its prey. A target gains no benefit from cover in an engagement with this creature as it smells the victim and senses its movement.', 'OaA:57'),
  (32, 'Bloodfly Sickness', 'A character who takes damage from a bloodfly bite must make an Average Resilience checks; failure results in 4 damage, ignoring soak, and the character halves his strain threshold, rounding up, util the character spends at least an hour in a bacta tank or undergoes similarly extensive medical treatment.', 'NoP:42'),
  (33, 'Blubber', '+1 soak', 'JoY:14'),
  (34, 'Born to Ride', 'Dugs are particularly adept rifders. The begin the game with one rank of the Defensive Driving talent.', 'SM:22'),
  (35, 'Brute Strength', 'May add [ADVANTAGE] to all Brawn-related skill checks.', 'LoNH:77'),
  (36, 'Camouflaged', 'When in jungle, choku add [BOOST}[BOOST] to all Stealth checks', 'SoR:59'),
  (37, 'Captain of the Wing Guard', 'May spend a maneuver giving orders to other Wing Guar allies in medium range, granting them [BOOST] on their next checks.', 'JoY:88'),
  (38, 'CDE-T Tactical Computer', 'T-series tactical droids adds [BOOST][BOOST] when making Leadership and Knowledge [Warfare] checks', 'LbE:45'),
  (39, 'Cloak', 'Add [SETBACK][SETBACK][SETBACK] to all checks made to spot or track the maalraa.', 'F-CRB:416'),
  (40, 'Color-shifting Hide', 'Add [BOOST][BOOST] to Stealth checks', 'BtR:46'),
  (41, 'Companion Animal', 'Once per encounter, may make an Easy Charm check targeting an engaged character; that character recovers 1 strain per [SUCCESS]', 'SoR:27'),
  (42, 'Computer Affinity', 'May add [FORCE] to any Computers check or Skulduggery check to analyze or infiltrate a security system. Spend [FORCE POINT] to add [ADVANTAGE] to the check. Spend [FORCE POINT][FORCE POINT] to add [SUCCESS] to any Computers check.', 'F-CRB:411'),
  (43, 'Constabulary Honor 2', 'Add automatic [THREAT][THREAT] to all social checks made to convince an orange-rating constable to act against the best interest of Sullust', 'SoR:43'),
  (44, 'Constabulary Honor 3', 'Add automatic [THREAT][THREAT][THREAT] to all social checks made to convince an orange-rating constable to act against the best interest of Sullust', 'SoR:43'),
  (45, 'Constrictor', 'Opponents affected by the Ensnare quality immediately begin suffering the effects of suffocation as outlined on page 214 of E-CRB.', 'SoF:43'),
  (46, 'Convincing Demeanor +1', '[SPECIES] starts with one rank in Convincing Demeanor', 'E-CRB:44'),
  (47, 'Cornered Fury', 'May upgrade one skill die when engaged and outnumbered by enemies', 'A-CRB:449'),
  (48, 'Covering Fire', 'May spend a maneuver to add +1 ranged defense to up to two allied characters or minion groups within short range until the end of the next round. This does not stack with multiple uses.', 'JoY:12'),
  (49, 'Create Bacta', 'Vratix can secrete one liter of bacta per day', 'SoR:55'),
  (50, 'Creature of Illusion', 'Combat checks targeting Moraband Sith phantasms always use Willpower in place of the characteristic normally associated with that skill; the Pierce and Breach qualities do not reduce a Sith phantasm\'s soak', 'CotG:85'),
  (51, 'Creature of the Sea', '[SPECIES] are not capable of operating on land.', 'SoT:83'),
  (52, 'Critical Eye', 'Gain [BOOST] on all rolls made to duplicate a familiar document', 'A-CRB:424'),
  (53, 'Crushing Body', 'Any victim affected by the [SPECIES]\'s Ensnare quality suffers 2 strain and 1 wound ignoring soak at the beginning of their turn.', 'SoF:73'),
  (54, 'Crushing Grip', 'A [SELF] has many tenacles and can maintain a grip with some while attacking with others. When it hits an enemy, a [SELF} cen spend two Advantage [ADVANTAGE] to ensnare it in its tentacles. A trapped character may make a [HARD] Athletics check as an action to try and escape the [SELF]\'s grasping limbs; a character who succeeds wrestles free.', 'A-BGR:47'),
  (55, 'Crystalline', 'Does not need to breathe and can survive in vacuum and underwater; immune to poisons and toxins.', 'LoNH:60'),
  (56, 'Cunning Ambusher 1', 'Opponents add [SETBACK] to Initiative checks using Vigilance', 'NoP:22'),
  (57, 'Cybernetic communications', 'Due to their cybernetic modifications, Ganks can communicate silently with other Ganks', 'LoNH:57'),
  (58, 'Cybernetic Microscopic Vision', '[SELF] gains +1 ranks in Vigilance and Perception and can zoom in to see items and organisms in the microscopic realm', 'BtR:64'),
  (59, 'Cybernetics Expert', 'May ignore one [FAILURE] and [THREAT] when making Medicine check related to cybernetics installation, diagnostics, or alterations', 'BtR:64'),
  (60, 'Dark Side Force User', 'uses Dark Side results instead of Light Side results, see page 281 F-CRB)', 'F-CRB:412'),
  (61, 'Dark Vision', 'When making skill checks, Kel Dors remove up to [SETBACK][SETBACK] imposed due to darkness.', 'F-CRB:58'),
  (62, 'Darkest Dreams', 'A Moraband Sith phantasm can spend [2 ADVANTAGE] on a combat check or [@ THREAT] on an engaged enemy\'s combat check to have the target mak an Average fear check [upgraded once for the phantasm\'s terrifying nature]; if the target fails, the phantasm transforms into a figure inspired by the target\'s greatest fear in addition to any other effects of the failed check.', 'CotG:85'),
  (63, 'Death Lunge', 'When incapacitated, [SPECIES] make one out-of-turn Brawl attack against one opponent with whom they are engaged. This check gains [BOOST]', 'SoF:42'),
  (64, 'Dense Feathers', 'Immune to the effects of cold and hazardous weather', 'NoP:15'),
  (65, 'Destabilizing Influence', 'As a maneuver once per encounter, may add [THREAT] to all subsequent Cool and Discipline checks made by enemies within medium range for the remainder of the encounter', 'A-CRB:417'),
  (66, 'Detect Force-sensitivity', 'The vornskr may make an Average PErception check to detect all Force-sensitive creatures or characters witha Force rating within medium range.', 'F-CRB:417'),
  (67, 'Dive Attack', 'The first attack made during an encounter gains +2 damage and [BOOST].', 'SoF:69'),
  (68, 'Dive-bomb', 'As an action, may make an oppesed Coercion check vs. Cool against one character at up to medium range; on a success, the target is staggered for one round and the falcon moves to engaged with the target; [ADVANTAGE] may be spent to cause the target to suffer 1 strain, and [TRIUMPH] may be spent to extend the effect of the stagger by 1 round.', 'SoR:27'),
  (69, 'Domesticable 1', 'Downgrade difficulty of checks to train [SPECIES] by one', 'SoT:83'),
  (70, 'Domesticable 2', 'Downgrade the difficulty of checks to train [SPECIES] by two.', 'SoT:85'),
  (71, 'Domesticated', 'Difficulty for training this beast is not upgraded.', 'SoT:88'),
  (72, 'Drain Life', 'Whenever a shaoryn inflicts damage on a target after soak, it heals half that many wounds [rounded up]', 'LoNH:129'),
  (73, 'Droid', 'Does not need to breathe, eat, or drink and can survive in vacuum or underwater. Immune to poisons or toxins.', 'E-CRB:47'),
  (74, 'Droid Leader', 'May spend a maneuver giving orders to all droid allies in medium range, granting the [BOOST] on their next check.', 'GMK:8'),
  (75, 'Energy Parasite', '[PLURAL_SELF] can make an [AVERAGE] Coordination check to latch onto any starship or vehicle they are engaged with. Ships of Vehicles with a [SELF] attached suffer 1 system strain per [SELF] each day and reduce their handling by 1.', 'A-BGR:47'),
  (76, 'Energy Sensitivty', 'The head cones of a Gotal are finly tuned sensory organs used to detect almost the entirety of the electromagnetic spectrum. Once per encounter as a maneuver, a Gotal may sense the presence and current emotional states of all living things within short range of himself.', 'FC:22'),
  (77, 'Enhanced Senses', 'Add automatic [ADVANTAGE] to app Perception checks', 'A-CRB:422'),
  (78, 'Envenomed Bite', 'Whenever a bearsloth successfully attacks a target [wether or not it deals damage], the GM may flip a dark side Destiny Point to have the target suffer the effects of being exposed to bearsloth venom.', 'CotG:22'),
  (79, 'Etiquette and Protocol', '[SELF]s allow allies to add [BOOST] to any Negotiation checks or other checks made to negotiate or mediate.', 'E-CRB:412'),
  (80, 'Excellent Climber', 'Once per game session, the character mar reroll any one Athletics check made when climbing', 'MPQ:15'),
  (81, 'Fearless', '[SPECIES] are immune to the effects of fear.', 'SoT:86'),
  (82, 'Fearsome Aspect', 'Add [BOOST] to all Coercion checks.', 'CotG:67'),
  (83, 'Fearsome Countenance', 'Zabrak add automatic [ADVANTAGE] to all Coercion Checks they make.', 'F-CRB:63'),
  (84, 'Fecklen Boom', 'As an action, may make an Easy Survival Check; each [SUCCESS] causes one target in medium range to be disoriented for 1 round. Each [ADVANTAGE][ADVANTAGE] extends the number of rounds by 1', 'SoR:17'),
  (85, 'Fierce Pounce', 'If the [SPECIES] users the aim maneuver, it also gains Knockdown on its next attack', 'BtR:47'),
  (86, 'Fire Resistant', 'Immune to fire damage or weapons with the Burn quality', 'SoF:102'),
  (87, 'Fire Sweep', 'As an action, a droideka can make a Hard Gunnery check to inflict on hit that deals base damage with its built-in twin heavy blasters on up to two targets within medium range, plus one additional target per [ADVANTAGE] on the check; it can only hit each target once this way', 'CotG:69'),
  (88, 'Firefighter', 'May make an Average [difficulty][difficulty] Athletics check to extinguish a fire', 'SoF:102'),
  (89, 'Flyer', '[PLURAL_SELF] can fly', 'A-BGR:47'),
  (90, 'For Quolas!', 'As an action, make an Average Leadership check to add [BOOST] to all combat checks by allies within short range for one round.', 'CotG:31'),
  (91, 'Force Hunter', 'Roll [FORCE] on Initiative checks; [LIGHT]: gain [SUCCESS] per point on its initiative check', 'OaA:56'),
  (92, 'Force Mimic', 'At the GM\'s discresion, the kaaten may make and opposed Charm or Deception versus Vigilance check as an action to pretend to be a friend, companion, loved one, or of the same species as its target.', 'F-CRB:416'),
  (93, 'Force Power Battle Meditation', 'Telepathically guide allies', 'F-CRB:284'),
  (94, 'Force Power Bind', 'Telekinetic powers than can seize others from afar.', 'F-CRB:286'),
  (95, 'Force Power Enhance', 'Gain [SUCCESS] or [ADVANTAGE] on Athletics checks', 'A-CRB:298, F-BGA:41, F-CRB:288'),
  (96, 'Force Power Farsight', 'Expand normal cisual senses through a connection to the force.', 'SS:36'),
  (97, 'Force Power Forsee', 'Gain vague hints of events to come.', 'A-CRB:300, F-CRB:290'),
  (98, 'Force Power Heal/Harm', 'Manipulate the living energy in things around him.', 'F-BGA:41*, F-CRB:292'),
  (99, 'Force Power Influence', 'Inflict strain on target', 'E-CRB:282, F-CRB:294'),
  (100, 'Force Power Misdirect', 'Create illusions to fool those around him.', 'F-CRB:296'),
  (101, 'Force Power Move', 'Move objects at range.', 'E-CRB:284, F-BGA:42, F-CRB:298'),
  (102, 'Force Power Protect/Unleash', 'Guide the flow of energy', 'F-CRB:300'),
  (103, 'Force Power Seek', 'Allow the will of the force to lead the way.', 'F-CRB:302'),
  (104, 'Force Power Sense', 'Sense the Force interacting with the world.', 'F-BGR:43, F-CRB:304'),
  (105, 'Force Power Suppress', 'Can dampen the effect of incoming force powers.', 'KtP:38'),
  (106, 'Force Power Warde\'s Foresight', 'Can feel the Force flowing around another specific individual to gain insight into what that person might do.', 'CotG:10'),
  (107, 'Force Stalker', 'commit [FORCE]: gain [BOOST][BOOST] on Stealth checks', 'OaA:56'),
  (108, 'Force-Sensitive Negotiation', 'add [BOOST] to any Negotiation or Streetwise checks unless the target is immune to Foce powers.', 'F-BGR:46'),
  (109, 'Four-armed', 'The [SPECIES gains [BOOST] on all Brawl checks and may spend [ADVANTAGE][ADVANTAGE] on a successful melee attack to hit a second target engaged with it, dealing the same damage as dealt to the original target.', 'E-CRB:415'),
  (110, 'Frightening Visage', 'As an action, may make an opposed Deception check vs. Vigilance [when multiple opponents are present, the best Vigilance skill determines difficulty] to create illusion of greater size and threat. If the snowfeather succeeds, all opponents present must make a Hard fear check. Force-sensitive characters add [SETBACK] to the fear check; droids and other characters immune fo Force powers automatically pass the check.', 'NoP:15'),
  (111, 'Gand Discipline', 'One free rank of Discipline. May not train above rank 2 during character creation.', 'E-CRB:48'),
  (112, 'Glider', '[SPECIES] can suffer 2 strain to count as a flyer for the remainder of the turn.', 'SoT:89'),
  (113, 'Gun Crew', 'For each [SELF] member in the minion group beyond the first, reduce the Cumbersome rating of their weapons by 1', 'E-CRB:418'),
  (114, 'Hard of Hearing', 'Add [SETBACK] when attempting a Perception check based on auditory clues.', 'MPQ:30'),
  (115, 'Hard to Kill', 'The GM may flip aone dark side Destiny Point to have the Kintan Strider heal 5 wounds.', 'LoNH:64'),
  (116, 'Hardy', '[SPECIES] add [BOOST] to all Resilience checks.', 'SoF:20'),
  (117, 'Hardy Survivalist', 'A Whiphid adds automatic [SUCCESS] to Survival checks and can survive several weeks without food thanks to reserve blubber.', 'KtP:22'),
  (118, 'Heat Resistance', 'Remove up to [SETBACK] from any checks due to hot or arid environmental conditions.', 'F-CRB:418'),
  (119, 'Heightened Sense of Smell', 'Razhak upgrade all Perception checks once.', 'NoP:15'),
  (120, 'Helium Allergy', 'When exposed to helium, [PLURAL_SELF] suffer 1 wound per round, ignoring soak, until their bodies inflate and explode.', 'A-BGR:47'),
  (121, 'Hoverer', 'Floats up to several meters in the air and may move through difficult terrain without spending additional maneuvers.', 'EtU:57'),
  (122, 'Humming Vibrations', 'As an action, may make an Opposed Charm vs. Discipline check against a target at up to medium range to disorient the target for a number of rounds equal to [SUCCESS]; may spend [ADVANTAGE][ADVANTAGE][ADVANTAGE] or [TRIUMPH] to stagger the target for 1 round instead', 'LoNH:87'),
  (123, 'Huttese', 'Hutt security droid neither speaks nor understands Basic', 'LoNH:144'),
  (124, 'Imperial Haughtiness', 'When a non-humanoid alien species is targeting [SELF] with a social skill check, add [SETBACK].', 'JoY:64'),
